-- 1. Create the database
CREATE DATABASE IF NOT EXISTS Business_Contacts;

-- 2. Create the user and grant privileges
CREATE USER IF NOT EXISTS 'businessuser'@'%' IDENTIFIED BY 'businesspassword';
GRANT ALL PRIVILEGES ON Business_Contacts.* TO 'businessuser'@'%';
FLUSH PRIVILEGES;

-- 3. Use the database
USE Business_Contacts;

-- 4. Create the Users table
CREATE TABLE IF NOT EXISTS contacts (
    id INT AUTO_INCREMENT PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    company VARCHAR(100) NOT NULL,
    email VARCHAR(100),
    phone VARCHAR(100), 
    address VARCHAR(100),
    city VARCHAR(100),
    state VARCHAR(100),
    zip_code VARCHAR(100),
    notes TEXT
);

